package com.emis.db;import java.sql.Connection;import java.sql.SQLException;/** * emisDbConnector interface 定義了一個 Connection Spool 所應該有 * 的功能 , emisConnectProxy 則是一個 Connection Cache * @see com.emis.db.emisConnectProxy */public interface emisDbConnector{  /**   *  從 Database Spool 拿取一個 Connection Cache (Proxy)   */  emisConnectProxy getConnection() throws Exception ;  /**   * 取得 Database 的 charset   */  String getCharset() ;  /**   *  資料庫與系統間不轉碼   */  public static final int TRANSFER_NONE = 0;  /**   *  資料庫到系統間轉碼   */  public static final int TRANSFER_DB_TO_SYS = 1;  /**   *  系統到資料庫間轉碼   */  public static final int TRANSFER_SYS_TO_DB = 2;  /**   *  資料庫與系統間雙向轉碼   */  public static final int TRANSFER_ALL = 3;  int getCharTransferMode();  emisEncodingTransfer getEncodingTransfer();  String errToMsg( SQLException e );  boolean isPKError(SQLException e);  public String dualTestSQL();  String getSequenceNumber(Connection oConn,String sSequenceName,boolean checkAutoDrop,String sDropCondition,String sFormat) throws SQLException;  String getStoreSequence(Connection oConn,String sSequenceName,String sSNO,String sDropCondition,String sFormat) throws SQLException;  void expireSQLCache(Connection oConn,String sSQLName) throws SQLException;  /**   * 获取数库的类型(如一种驱动可面向多种不同数据请写不同的Impl)   * @return   */  public String getDBType();}