package com.emis.db;import java.sql.SQLException;/** *  用來做 SQL Top 10 記錄的資料結構 class */public class emisSQLRec{	  public String sSQL;  public long nExecTime;  public long nStartTime;  public SQLException m_sqlerr;  public emisSQLRec(String SQL,long startTime,long execTime) {    sSQL = SQL;    nExecTime = execTime;    nStartTime = startTime;  }    public emisSQLRec(String SQL,long startTime,long execTime,SQLException sqlerr) {    sSQL = SQL;    nExecTime = execTime;    nStartTime = startTime;    m_sqlerr = sqlerr;  }}